import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [properties, setProperties] = useState([]);
  const [filters, setFilters] = useState({ price: "", bedrooms: "" });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch data from the backend
    setLoading(true);
    axios.get("http://127.0.0.1:5000/api/properties")
      .then((res) => {
        console.log("Data received:", res.data);
        setProperties(res.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error fetching properties:", error);
        setError("Failed to fetch properties. Using sample data instead.");

        // Fallback to sample data from properties.json
        const sampleData = [
          {
            "name": "Cozy Cottage",
            "price": 150000,
            "location": "Springfield",
            "bedrooms": 2,
            "bathrooms": 1,
            "image_url": "https://images.pexels.com/photos/1643389/pexels-photo-1643389.jpeg"
          },
          {
            "name": "Modern Apartment",
            "price": 250000,
            "location": "Metropolis",
            "bedrooms": 3,
            "bathrooms": 2,
            "image_url": "https://images.pexels.com/photos/259950/pexels-photo-259950.jpeg"
          },
          {
            "name": "Spacious Villa",
            "price": 500000,
            "location": "Beachside",
            "bedrooms": 4,
            "bathrooms": 3,
            "image_url": "https://images.pexels.com/photos/2102587/pexels-photo-2102587.jpeg"
          },
          {
            "name": "Urban Loft",
            "price": 300000,
            "location": "Downtown",
            "bedrooms": 2,
            "bathrooms": 2,
            "image_url": "https://images.pexels.com/photos/439391/pexels-photo-439391.jpeg"
          }
        ];

        setProperties(sampleData);
        setLoading(false);
      });
  }, []);

  const applyFilters = (prop) => {
    return (
      (filters.price === "" || prop.price <= filters.price) &&
      (filters.bedrooms === "" || prop.bedrooms === parseInt(filters.bedrooms))
    );
  };

  return (
    <div className="container">
      <h1>Property Listings</h1>

      {error && <div className="error-message">{error}</div>}

      <div className="filters">
        <input
          type="number"
          placeholder="Max Price"
          onChange={(e) => setFilters({ ...filters, price: e.target.value })}
        />
        <input
          type="number"
          placeholder="Bedrooms"
          onChange={(e) => setFilters({ ...filters, bedrooms: e.target.value })}
        />
      </div>

      {loading ? (
        <div className="loading">Loading properties...</div>
      ) : (
        <div className="listings">
          {properties.filter(applyFilters).map((property, index) => (
            <div key={property.id || property.name || index} className="card">
              <img src={property.image_url} alt={property.name} />
              <h2>{property.name}</h2>
              <p>Price: ${property.price}</p>
              <p>Location: {property.location}</p>
              <p>Bedrooms: {property.bedrooms}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
